package homeworks.hw1;import java.util.List;public class task4 {    public static void main(String[] args) {        System.out.println(getSolution("?1 + 3? = 59"));        System.out.println(getSolution("1? + ?3 = 59"));        System.out.println(getSolution("1 + ? = 9"));        System.out.println(getSolution("? + 2 = 9"));        System.out.println(getSolution("2 + ? = 9"));        getSolution("? + 7 = 9");        getSolution("?5 + 4? = 21");        getSolution("?2 + ?5 = 27");        getSolution("?1 + 3? = 59");        getSolution("1? + ?3 = 95");    }    public static String getSolution(String str) {        int index1 = str.indexOf('?');        int index2 = str.lastIndexOf('?');        String strZero = str.replace("?", "0");        List<String> arr = List.of(strZero.split(" "));        int leftNumber = Integer.parseInt(arr.get(0));        int rightNumber = Integer.parseInt(arr.get(2));        int sum = Integer.parseInt(arr.get(4));        int newNum1 = (sum - (leftNumber + rightNumber)) / 10;        int newNum2 = (sum - (leftNumber + rightNumber)) % 10;        System.out.printf("str: %s, newNum1: %s, newNum2: %s%n", str, newNum1, newNum2);        if (newNum1 < 0 || newNum2 < 0) {            return "No solution";        } else if (index1 == 0) {            return String.format("%s + %s = %s%n", sum - rightNumber, rightNumber, sum);        } else if (newNum1 + rightNumber / 10 == sum / 10) {            return String.format("%s + %s = %s%n", newNum1 * 10 + leftNumber, rightNumber + newNum2, sum);        } else if (leftNumber / 10 + newNum1 == sum / 10) {            return String.format("%s + %s = %s%n", leftNumber + newNum2, newNum1 * 10 + rightNumber, sum);        }        return "No solution";    }}//            if (index1 == 0 && index2 != 4) {//                return String.format(" %s + %s = %s", new1 * 10 + n1, new2 + n2, sum);//            } else if (index1 == 1) {//                int ans1 = n1 + new2 + new1 * 1