package homeworks.hw2;//QUERY = "select * from students where ";//        PARAMS = "{\"name\":\"Ivanov\", \"country\":\"Russia\", \"city\":\"Moscow\", \"age\":\"null\"} ";public class Task1 {    public static void main(String[] args) {        String QUERY = "";        String PARAMS = "";        if (args.length == 0) {            // При отправке кода на Выполнение, вы можете варьировать эти параметры            QUERY = "select * from students where ";            PARAMS = "{\"name\":\"Ivanov\", \"country\":\"Russia\", \"city\":\"Moscow\", \"age\":\"null\"} ";        } else {            QUERY = args[0];            PARAMS = args[1];        }        Answer ans = new Answer();        System.out.println(ans.answer(QUERY, PARAMS));    }}class Answer {    public static StringBuilder answer(String QUERY, String PARAMS) {        // Напишите свое решение ниже        StringBuilder bilder = new StringBuilder();        bilder.append(QUERY);        String[] arrStr = PARAMS.replace("{", "")                .replace("}", "")                .split(",");        for (int i = 0; i < arrStr.length; i++) {            if (!arrStr[i].contains("null")) {                if (i > 0) {                    bilder.append(" and");                }                String[] res = arrStr[i].split(":");                res[0] = res[0].replace("\"", "");                res[1] = res[1].replace("\"", "'");                bilder.append(res[0]).append("=").append(res[1]);            }        }        return bilder;    }}