package homeworks.hw4;import java.util.ArrayDeque;class Calculator {    private static ArrayDeque<Integer> results;    public Calculator(){        results = new ArrayDeque<>();    }    public static int calculate(char op, int a, int b) {        int result = 0;        if (op == '+')            result = a + b;        else if (op == '-')            result =  a - b;        else if (op == '*')            result =  a * b;        else if (op == '/')            result =  a / b;        if(op == '<'){            results.removeLast();            result = results.peekLast();        }else {            results.add(result);        }        return result;    }}