package homeworks.hw5;import java.util.Arrays;// Реализация пирамидальной сортировки на Java// Java implementation of Iterative Heap Sortpublic class HeapSort {    // function build Max Heap where value// of each child is always smaller// than value of their parent    static void buildTree(int tree[], int sortLength)    {        for (int i = 1; i < sortLength; i++)        {            // if child is bigger than parent            if (tree[i] > tree[(i - 1) / 2])            {                int j = i;                // swap child and parent until                // parent is smaller                while (tree[j] > tree[(j - 1) / 2])                {                    swap(tree, j, (j - 1) / 2);                    j = (j - 1) / 2;                }            }        }    }    static void heapSort(int sortArray[], int sortLength)    {        buildTree(sortArray, sortLength);        for (int i = sortLength - 1; i > 0; i--)        {            // swap value of first indexed            // with last indexed            swap(sortArray, 0, i);            // maintaining heap property            // after each swapping            int j = 0, index;            do            {                index = (2 * j + 1);                // if left child is smaller than                // right child point index variable                // to right child                if (index < (i - 1) && sortArray[index] < sortArray[index + 1])                    index++;                // if parent is smaller than child                // then swapping parent with child                // having higher value                if (index < i && sortArray[j] < sortArray[index])                    swap(sortArray, j, index);                j = index;            } while (index < i);        }    }    public static void swap(int[] a, int i, int j) {        int temp = a[i];        a[i]=a[j];        a[j] = temp;    }    /* A utility function to print array of size n */    static void printArray(int arr[])    {        int n = arr.length;        for (int i = 0; i < n; i++)            System.out.print(arr[i] + " ");        System.out.println();    }    // Driver program    public static void main(String args[])    {        int arr[] = {10, 20, 15, 17, 9, 21};        int n = arr.length;        System.out.print("Initial array: \n");        // printArray(arr);        System.out.println(Arrays.toString(arr));        heapSort(arr, n);        System.out.print("Sorted array: \n");        // printArray(arr);        System.out.println(Arrays.toString(arr));    }}