package org.seminars;import java.time.LocalTime;import java.util.Scanner;public class Task1 {    public static void main(String[] args) {        hello();    }    public static void hello() {        System.out.println("Введите ваше имя и фамилию: ");        Scanner scanner = new Scanner(System.in);        String name = scanner.next();        String surname = scanner.next();        LocalTime currentTime = LocalTime.now();        if (currentTime.isAfter(LocalTime.of(5, 0)) &&                currentTime.isBefore(LocalTime.of(12, 0))) {            System.out.printf("Доброе утро! %s %s%n", name, surname);        } else if (currentTime.isAfter(LocalTime.of(12, 1)) &&                currentTime.isBefore(LocalTime.of(18, 0))) {            System.out.printf("Добрый день! %s %s%n", name, surname);        } else if (currentTime.isAfter(LocalTime.of(18, 1)) &&                currentTime.isBefore(LocalTime.of(23, 0))) {            System.out.printf("Добрый день! %s %s%n", name, surname);        } else {            System.out.printf("Доброй ночи! %s %s%n", name, surname);        }    }}