// Напишите метод, который сжимает строку.// Пример: aaaabbbcdd → a4b3cd2package org.seminars.seminar2;import java.util.ArrayList;import java.util.List;public class task2 {    public static void main(String[] args) {        System.out.println(reduceStr("aaaabbbcdd"));        System.out.println(reduceStr("aaaabbbcddaaabbb"));    }    public static String reduceStr(String str) {        String[] arr = str.split("");        // char[] chars = str.toCharArray();        StringBuilder stringBuilder = new StringBuilder();        List<String> tmp = new ArrayList<>();        for (int i = 0; i < arr.length; i++) {            if (i == 0) {                tmp.add(arr[i]);            } else {                if (arr[i].equals(arr[i - 1])) {                    tmp.add(arr[i]);                } else {                    stringBuilder.append(arr[i - 1]).append(tmp.size());                    tmp.clear();                    tmp.add(arr[i]);                }            }            if (i == arr.length - 1) {                stringBuilder.append(tmp.get(0)).append(tmp.size());            }        }        return stringBuilder.toString();    }}//    public static void reduce(String arg) {//        int count = 1;//        String[] arr = arg.split("");//        StringBuilder result = new StringBuilder();//        for (int i = 0; i < arr.length; i++) {//            if (i == 0) {//                result.append(arr[i]);//                continue;//            }////            if (arr[i].equals(arr[i - 1])) {//                count ++;//            } else if (!(arr[i].equals(arr[i - 1])) && count != 1){//                result.append(count).append(arr[i]);//                count = 1;//            } else {//                result.append(arr[i]);//            }//        }//        if (count != 1) {//            result.append(count);//        }//        System.out.println(result);//    }