package org.seminars.seminar3;import java.util.ArrayList;import java.util.List;public class SolarSystemPlanet {    static List<String> planet = new ArrayList<>();    public static void main(String[] args) {        fillPlanet();        printPlanet(planet);    }    public static void fillPlanet() {        planet.add("Earth");        planet.add("Earth");        planet.add("Earth");        planet.add("Mars");        planet.add("Venus");        planet.add("Venus");    }    public static void printPlanet(List<String> planets) {        StringBuilder stringBuilder = new StringBuilder();        String name = planets.get(0);        int count = 0;        for (String newName : planets) {            if (name.equals(newName)) {                count++;            } else {                stringBuilder.append(name).append(" ").append(count).append("\n");                name = newName;                count = 1;            }        }        stringBuilder.append(name).append(" ").append(count).append("\n");        System.out.println(stringBuilder);    }}