package org.seminars.seminar3;import java.util.ArrayList;import java.util.LinkedList;import java.util.Random; // 400 1002public class Task1 {    public static void main(String[] args) {        ArrayList<Integer> arrList = new ArrayList<>();        LinkedList<Integer> linkList = new LinkedList<>();        Random random = new Random();        long startTime = System.currentTimeMillis();        for (int i = 0; i < 10_000_000; i++) {            arrList.add(i);        }        long end = System.currentTimeMillis();        System.out.println("time: " + (end - startTime));        long startTime1 = System.currentTimeMillis();        for (int i = 0; i < 10_000_000; i++) {            linkList.add(i);        }        long end1 = System.currentTimeMillis();        System.out.println("time: " + (end1 - startTime1));        int count = 0;        long startTime3 = System.currentTimeMillis();        while (count != 1000){            linkList.add(4000, random.nextInt());            count++;        }        long end3 = System.currentTimeMillis();        System.out.println("time: " + (end3 - startTime3));    }}