package org.seminars.seminar6;import java.util.HashSet;import java.util.List;public class Cat {    String name;    double weight;    double age;    String breed;    String owner;    String gender;    int prizeTime;    public Cat(String name, double weight, double age) {        this.name = name;        this.weight = weight;        this.age = age;    }    @Override    public String toString() {        return String.format("name = %s, weight = %s, age = %s", name, weight, age);    }    public boolean equals(Object cat) {        Cat newCat = (Cat) cat;        return this.name.equals(newCat.name) &&                this.age == newCat.age &&                this.weight == newCat.weight;    }    public int hashCode() {        return (int) (this.name.hashCode() + this.age + this.weight);    }    public static void main(String[] args) {        Cat cat = new Cat("Barsik", 3, 6.2);        Cat cat2 = new Cat("Muska", 2, 5.2);        Cat cat3 = new Cat("Barsik", 3, 6.2);        System.out.println(cat);        HashSet<Cat> data = new HashSet<>(List.of(cat, cat2, cat3));        System.out.println(data);        for (Cat c: data) {            System.out.println(c.name);        }    }}